console.log("hello world");const handleTheme=()=>{const t=document.querySelector("body");document.querySelectorAll(".theme-light").forEach(e=>{e.addEventListener("click",()=>{t.classList.add("light")})}),document.querySelectorAll(".theme-dark").forEach(e=>{e.addEventListener("click",()=>{t.classList.remove("light")})}),document.querySelectorAll(".nav .theme-light, .nav .theme-dark").forEach(e=>{e.addEventListener("click",()=>{document.querySelector("header input").checked=!1})})};handleTheme();